root_commands:
echo '#!/bin/bash
# Example x402 seller server setup
cat << '"'"'EXAMPLE'"'"'
import { paymentMiddleware } from "@x402/express";
import { facilitatorClient } from "@coinbase/x402";
import express from "express";

const app = express();

// Testnet configuration
const facilitator = facilitatorClient({
  url: "https://x402.org/facilitator",
});

// Protected endpoint requiring payment
app.get("/premium-content",
  paymentMiddleware(facilitator, {
    payTo: "0xYourWalletAddress",
    amount: "100000", // 0.10 USDC (6 decimals)
    asset: "USDC",
    network: "eip155:84532", // Base Sepolia
  }),
  (req, res) => {
    res.json({ content: "Premium content here!" });
  }
);

app.listen(3000);
EXAMPLE' > /home/agent/.x402-dev/scripts/example-seller.sh
